  render() {
    return (
      <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12" style={{backgroundColor:"#f8f8f8"}}>
        <div className="col-xs-12 col-sm-12 col-md-6 col-lg-6">
          <JobsCards selectCard={()=>this.selectCard(card)}/>
        </div>
        <div className="col-xs-12 col-sm-12 col-md-6 col-lg-6">
          <CardInfo />
        </div>
      </div>
    )
  }
}

export default Container
