import React, { Component } from "react";
import axios from 'axios';
import InfiniteScroll from 'react-infinite-scroller';


class JobsCards extends Component {

  state={
    jobsCardArr:[],
    hasMore:true
  }

  componentDidMount = () => {
    axios.get("https://api.for9a.com/opportunity/filter?count=12&page=1&type=29").then(res => {
      console.log("our res eq", res)
    }).catch(err => {
      console.log(err)
    })
  }
  loadMoreJobs =()=>{
    if(!this.state.hasMore){
      return
    }

  }
  render() {
    return (
      <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
        <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12" style={{padding:"0px"}}>
          <InfiniteScroll 
          pageStart={0}
          initialLoad={false}
          loadMore={()=>{
            this.loadMoreJobs()
          }}
          hasMore={this.state.hasMore}
          loader={
            <div>hhhh</div>
          }
          >

          </InfiniteScroll>
        </div>

      </div>
    )
  }
}

export default JobsCards
